mapper.select:
SELECT a.news_media_id newsMediaId, a.quote_price quotePrice, a.title title, a.area_id areaId, a.allow_link allowLink, a.allow_qrcode allowQRCode, a.allow_contactway allowContactWay, a.image_url imageUrl, a.include_type includeType, a.remark remark, a.audit_status auditStatus, b.channel_name mediaName, c.name mediaType, d.news_column_name mediaColumn, a.created_date createdate, e.login_name creator, b.link_url linkUrl FROM media_news_info a, media_news_channel b, media_news_type c, media_news_column d, member_info e WHERE a.channel_id = b.channel_id AND b.news_type = c.id AND a.column_id = d.news_column_id AND a.created_user = e.member_id
AND a.created_user = #{createdUser}
ORDER BY a.audit_status, a.created_date DESC
limit #{beginLimit}, #{endLimit}
