mapper.select:
select a.wechat_media_id wechatMediaId, a.first_price firstPrice, a.second_price secondPrice, a.other_price otherPrice, a.remark remark, a.audit_status auditStatus, b.nickname nickname, b.account account, b.qr_code qrCode, b.head_image headImage, b.`desc` `desc`, b.authentication authInfo, b.fans_count fansCount, c.name typeName, d.area_name areaName, e.login_name creator, a.created_date createdate from media_wechat_info a, media_wechat_channel b, media_wechat_type c, system_area_info d, member_info e where a.channel_id = b.channel_id and b.type_id = c.id and d.area_id = a.area_id and a.created_user = e.member_id
and a.created_user = #{createdUser}
and b.nickname like CONCAT('%',#{mediaName},'%' )
and a.audit_status = #{auditStatus}
order by a.audit_status, a.created_date desc
limit #{beginLimit}, #{endLimit}

select count(a.wechat_media_id) from media_wechat_info a, media_wechat_channel b, media_wechat_type c where a.channel_id = b.channel_id and b.type_id = c.id
and a.created_user = #{createdUser}
and b.nickname like CONCAT('%',#{mediaName},'%' )
and a.audit_status = #{auditStatus}

SELECT g.*, b.nickname nickname, b.account account, b.qr_code qrCode, b.head_image headImage, c.name typeName, b.`desc` `desc`, b.authentication authInfo, b.fans_count fansCount, d.area_name areaName FROM ( SELECT a.channel_id channelId, a.wechat_media_id wechatMediaId, a.first_price firstPrice, a.second_price secondPrice, a.other_price otherPrice, a.area_id areaId, a.remark remark, a.created_date createdate FROM media_wechat_info a LEFT JOIN media_wechat_info f ON a.channel_id = f.channel_id AND a.first_price < f.first_price WHERE a.audit_status = 1 GROUP BY a.channel_id ) g, media_wechat_channel b, media_wechat_type c, system_area_info d WHERE g.channelId = b.channel_id AND b.type_id = c.id AND d.area_id = g.areaId
and b.nickname like CONCAT('%',#{mediaName},'%' )
and g.firstPrice > #{minPrice} and g.firstPrice < #{maxPrice}
and b.fansCount > #{minFansCount} and b.fansCount < #{maxFansCount}
ORDER BY g.createdate desc
limit #{beginLimit}, #{endLimit}

SELECT count( g.wechatMediaId ) FROM ( SELECT a.channel_id channelId, a.wechat_media_id wechatMediaId, a.first_price firstPrice, a.second_price secondPrice, a.other_price otherPrice, a.area_id areaId, a.remark remark FROM media_wechat_info a LEFT JOIN media_wechat_info f ON a.channel_id = f.channel_id AND a.first_price < f.first_price WHERE a.audit_status = 1 GROUP BY a.channel_id ) g, media_wechat_channel b, media_wechat_type c, system_area_info d WHERE g.channelId = b.channel_id AND b.type_id = c.id AND d.area_id = g.areaId
and b.nickname like CONCAT('%',#{mediaName},'%' )
and g.firstPrice >= #{minPrice} and g.firstPrice <= #{maxPrice}
and b.fansCount >= #{minFansCount} and b.fansCount <= #{maxFansCount}

mapper.update:
update media_wechat_info
channel_id = #{channelId}, area_id = #{areaId}, first_price = #{firstPrice}, second_price = #{secondPrice}, other_price = #{otherPrice}, remark = #{remark}
where wechat_media_id = #{wechatMediaId}
