mapper.select:
select
	member_company_id
from
	member_company


		member_id = #{memberId}


		and 
			biz_license = #{bizLicense}


		and
			`status` = #{status}

mapper.update:
update member_company
`status` = #{status},
audit_user = #{auditUser},
audit_date = #{auditDate},
audit_msg = #{auditMsg}
where member_company_id = #{memberCompanyId}

mapper.select:
select a.company_name companyName, a.biz_license bizLicense, a.`status` `status`, a.created_date createDate, b.login_name memberName from member_company a, member_info b where a.member_id = b.member_id
and a.`status` = #{auditStatus}
limit #{beginLimit}, #{endLimit}
